INSERT INTO `about` (`id`, `title`, `text`, `cols`, `str`) VALUES
(1, '<i>Компания «Гидродинамика»</i> (г. Минск, Республика Беларусь)', '<p class=\'monotype\'>Товарный знак Закрытого акционерного общества «Гидродинамика» - символ экономичной, надежной и конкурентоспособной продукции, востребованной в республике Беларусь и на рынках СНГ.</p> \r\n<p class=\'monotype\'>Сегодня ЗАО «Гидродинамика» известно как одно из ведущих предприятий Российской ассоциации производителей насосов по разработке, производству, сопровождению в эксплуатации и ремонту герметичных и высоконадежных насосов для нефтегазовой отрасли, как в области добычи, транспортировки, так и переработки углеводородов. Насосные агрегаты производства ЗАО «Гидродинамика» эксплуатируются ведущими нефтегазодобывающими компаниями России и других стран.</p>\r\n<p class=\'monotype\'>Благодаря сочетанию интеллектуального потенциала, накопленного в течение работы ведущей части коллектива в проектировании и строительстве мобильных атомных станций, высокой корпоративной культуре, творческой атмосфере, широкой информированности ЗАО «Гидродинамика» выпускает конкурентоспособные насосы, по ряду параметров превосходящие зарубежные аналоги. Разрабатываемые и внедряемые на предприятии прогрессивные конструкторско - технологические решения опираются на новейшие достижения науки и техники. Тесное сотрудничество с разработчиками и многочисленными партнерам позволяет ЗАО «Гидродинамика» создавать насосы  уникальные по своим техническим и эксплуатационным характеристикам.</p>\r\n<p class=\'monotype\'>Приветствуем наших традиционных партнеров и всегда рады новым, заинтересованных  в совместных работах и взаимовыгодном сотрудничестве!</p>\r\n<p class=\'monotype\'>С наилучшими пожеланиями А.С. Субботин, Генеральный директор ЗАО «Гидродинамика».</p>', 1, 1),
(2, 'Насосные агрегаты (типа ГДМ)', '<p>Насосные агрегаты (типа ГДМ) нашей компании обеспечивают перекачку таких продуктов, как: стабильный газовый конденсат, нефть и нефтепродукты, диэтиленгликоль, аммиак, ШФЛУ, вода и водометанольная смесь, бензин, дизельное топливо с расходами от 1,6 до 1250 м<sup>3</sup>/час при напорах от 18 до 1650 м. и более -  для чего разработано более 60 моделей горизонтальных насосов, равномерно охватывающих своими техническими параметрами указанный диапазон.</p> \r\n<p>Для откачки жидкостей из цистерн и технологических емкостей разработаны и освоены в производстве свыше 30 моделей полупогружных агрегатов, обеспечивающих при глубине погружения от 2,7 до 6,2 м работу с подачами от 10 до 200 м<sup>3</sup>/час и напорами до 350 м.</p>\r\n', 2, 1),
(3, '', '<p class=\'italic\'>По сравнению с герметичными насосами на основе встроенных электродвигателей специальной конструкции, применяемых в насосных агрегатах, представленная схема насосных агрегатов ЗАО «Гидродинамика» имеет более простую, гибкую, пригодную для ремонтов в эксплуатационных условиях конструкцию, позволяет применять относительно дешевые и экономичные стандартные приводные электродвигатели, легко заменяя их при ремонтах.</p>\r\n<p class=\'italic\'>Появлению работоспособной конструкции магнитной муфты, передающей большие мощности, предшествовала большая экспериментально-конструкторская работа по оптимизации параметром элементов магнитной муфты, подбору магнитов по геометрическим размерам и характеристикам магнитных материалов.</p>', 2, 2),
(4, 'Магнитная муфта', '<p>Для вращения роторов насосных агрегатов типа ГДМ применяются герметичные магнитные муфты с разделительным экраном. Применение в приводе насоса магнитной муфты исключает выход приводного вала за пределы проточной части насоса и необходимость уплотнения вращающегося вала сальниковыми или торцовыми уплотнениями, не исключающими протечек перекачиваемого продукта.</p>\n<div id=\'img_box_left\'><img src=\"img/dummies/schema_muft_bw.png\" alt=\"Image\" class=\"left-align boxed\"/></div>\n<p class=\'italic\'>Как видно из схемы, мощность от электродвигателя передается на насос через магнитную муфту, которая посредством магнитных сил приводит во вращение ротор насоса. Магнитная муфта состоит из наружной полумуфты, соединенной с приводным электродвигателем, внутренней полумуфты, соединенной с ротором насоса, и раздели-тельного экрана, обеспечивающего герметизацию проточной полости агрегата.</p>', 3, 1),
(5, '', '<p>Решение элементарной на первый взгляд задачи по защите от коррозионного разрушения магнитов на основе неодим-железа-бора, происходившего периодически на первоначальных конструкциях муфт, потребовало не только менять поставщиков магнитов, но и кардинальным образом корректировать конструкцию и технологию изготовления магнитных полумуфт. В новых муфтах защиту магнитов от коррозии обеспечивают: трехслойное гальваническое покрытие магнитов, изолирующие магниты от перекачиваемого продукта, специальные защитные обечайки и специальная технология изготовления.</p>\r\n<p>Проверенная экспериментальными исследованиями отработанная методика расчета магнитных муфт дает возможность проектировать магнитные муфты для различных температурных условий: от -60°С до +250°С и на разные мощности с ограниченным количеством типоразмеров применяемых магнитов и марок магнитных материалов.</p>', 3, 2),
(6, '', '<p>В процессе доработки конструкции магнитного блока были выявлены довольно значительные осевые силы, возникающие между внутренней и наружной полумуфтой. Недооценка этих сил и непринятие конструктивных мер по их минимизации оказывают существенное влияние на баланс осевых сил  и нагруженность упорных пят насосного агрегата.</p>\r\n<p>Один из основных элементов магнитной муфты - разделительный экран, обеспечивающий герметизацию проточной части насосного агрегата и циркуляцию части перекачиваемого продукта для охлаждения магнитной муфты. Конструктивному и технологическому улучшению этого элемента постоянно уделяется пристальное внимание.                   Для уменьшения потерь в экране специалисты компании проводят научно - исследовательские и опытно – конструкторские работы по подбору новых видов материалов. Созданный опытный тонкостенный экран из неметаллического материала выдерживает внутреннее давление до 6 МПа и температуру до 100°С.</p>', 3, 3),
(7, '', '<p>Разработанный и доведенный ряд магнитных муфт обеспечивает в насосных агрегатах типа ГДМ передачу на ротор насоса от 1,6 до 630 кВт мощности от взрывозащищенных электродвигателей.</p>', 3, 2);
